//Draws a keyboard in alphabetical order
//Includes 26 letters, a delete button, and a continue button
void drawKeyboard()
{
    //Initialize variables
    int width = 20, height = 20, space = 3;             //width, height - Rectangle button dimensions; space - Space between buttons
    int startY = 96;                                    //Starting y-coordinate for the keyboard
    int startX, keys;                                   //Declare starting x-coordinate and number of keys per row
    LCD.SetFontColor(SCARLET);                          
    LCD.WriteAt("Enter a name", 110, 20);               //Prompt user to enter a name
    LCD.SetFontColor(WHITE);

    //Draw keyboard
    for (int r = 0; r < 3; r++)
    {
        if (r == 0 || r == 1)   //10 keys on the first two rows
        {
            keys = 10;  
            startX = 47;
        }
        else if (r == 2)        //6 keys on the third row
        {
            keys = 6;
            startX = 97;
        }
        for (int c = 0; c < keys; c++) //Draw the keys
        {
            LCD.DrawRectangle(startX + c * (space + width), startY + r * 25, width, height);
        }
    }
    
    LCD.DrawRectangle(145, 170, width * 2, height + 3);              //Draw delete key 
    LCD.DrawRectangle(120, 205, width * 4, height + 3);             //Draw continue key


    //Draw keyboard in alphabetical order
    LCD.WriteAt("A   B   C   D   E    F   G   H   I     J", 50, 100);
    LCD.WriteAt("K    L   M  N   O  P   Q   R   S    T", 50, 125);
    LCD.WriteAt("U   V   W   X   Y   Z", 100, 150);
    LCD.WriteAt("DEL", 150, 175);                 
    LCD.WriteAt("Continue", 125, 210);

    LCD.DrawHorizontalLine(50 + height, 80, 240);                   //Draw a line for the user to write their name on
}


