//Displays the player's statistics for their most recent game
//Two - Pointer to Player object
//played - Keeps track of if a game was just played in order to update the leaderboard accordingly
void stats(Player *Two, bool played)
{
    char names[6][13] = {"PlayerOne", "PlayerTwo", "PlayerThree", "PlayerFour", "PlayerFive", "Player"};
    char tempName[13] = "";
    char difficulty[13] = "";
    int scores[6] = {0, 0, 0, 0, 0, 0};
    int accuracy[6] = {0, 0, 0, 0, 0, 0};
    int tempScore, tempAcc, swaps;

    //Changes difficulty to the previous gamemode played
    switch ((*Two).showMode())                          
    {
        case 1:
            strcpy(difficulty, "Easy");         //Mode: Easy
        break;

        case 2:
            strcpy(difficulty, "Medium");       //Mode: Medium
        break;

        case 3:
            strcpy(difficulty, "Difficult");    //Mode: Difficult
        break;

        case 4:
            strcpy(difficulty, "Insane");       //Mode: Insane
        break;

        default:
            strcpy(difficulty, "N/A");          //No game played recently
        break;

    }


    LCD.Clear();
    LCD.WriteAt("Statistics", 120, 20);                     //Write "Statistics" at the top of the page
    LCD.SetDrawColor(GRAY);
    LCD.DrawHorizontalLine(40, 0, maxX);
    LCD.WriteAt("Name: ", 70, 50);                          //Show Name
    LCD.WriteAt((*Two).showName(), 130, 50);                //Write player name
    LCD.WriteAt("Mode: ", 70, 70);                          //Show mode
    LCD.WriteAt(difficulty, 130, 70);                       //Write mode
    LCD.WriteAt("Score: ", 70, 90);                         //Show score
    LCD.WriteAt((*Two).showScore(), 130, 90);               //Write score
    LCD.WriteAt("Accuracy: ", 70, 110);                     //Show player accuracy
    LCD.WriteAt((*Two).showAcc(), 160, 110);
    LCD.WriteAt("%", 190, 110);
    LCD.DrawHorizontalLine(215, 0, maxX);
    
    //Similar to the returnMain() function, but specific to this page
    LCD.WriteAt("Leaderboard", 105, 195);                           //Write "Leaderboard" at the bottom of the screen
    LCD.WriteAt("Return to Menu", 95, 220);                        //Write "Return to Menu" at the bottom of the screen


    loop = 0;
    LCD.ClearBuffer();


    FEHFile *ptr;                                           //Create leaderboard FEHFile pointer
    
    //Updates the leaderboard file after a game is played
    if (played)
    {
        played = false;                                     //Game is over, reset played to false
        switch ((*Two).showMode())
        {
            case 1:
                ptr = SD.FOpen("LB_Easy.txt", "a+");        //Opens Mode: Easy leaderboard file for reading
            break;

            case 2:
                ptr = SD.FOpen("LB_Med.txt", "a+");         //Opens Mode: Medium leaderboard file for reading
            break;

            case 3:
                ptr = SD.FOpen("LB_Diff.txt", "a+");        //Opens Mode: Difficult leaderboard file for reading
            break;

            case 4:
                ptr = SD.FOpen("LB_Ins.txt", "a+");         //Opens Mode: Insane leaderboard file for reading
            break;
        }

        
        //Read in the leaderboard scores
        int i = 0;
        while (!SD.FEof(ptr))
        {
            SD.FScanf(ptr, "%s%d%d", names[i], &scores[i], &accuracy[i]);      //Read in all of the leaderboard scores into arrays
            if (!SD.FEof(ptr))  
            {
                i++;    //Increment array index
            }
        }

        SD.FClose(ptr);     //Close file

        //If the leaderboard is empty, fill it
        if (i != 4)
        {
            for (int j = i; j < 4; j++)
            {
                strcpy(names[j],"Player");                          
                scores[j] = 0;
                accuracy[j] = 0;
            }
        }

        //Fill the 6th slot of the arrays with the player's most recent game data
        scores[5] = (*Two).showScore();
        strcpy(names[5], (*Two).showName());
        accuracy[5] = (*Two).showAcc();

        
        //Bubble sort algorithm to sort the scores and its corresponding player name and accuracy data
        do { 
            swaps = 0;
            for(int m = 0; m < 5; m++){
                if (scores[m+1] > scores[m])
                {
                    //Move player names around
                    strcpy(tempName, names[m]);
                    strcpy(names[m], names[m+1]);
                    strcpy(names[m+1], tempName);

                    //Sort score data
                    tempScore = scores[m];
                    scores[m] = scores[m+1];
                    scores[m+1] = tempScore;

                    //Move accuracy data around
                    tempAcc = accuracy[m];
                    accuracy[m] = accuracy[m+1];
                    accuracy[m+1] = tempAcc;

                    swaps = 1;
                }
                
                //Tiebreaker: Highest accuracy
                else if (scores[m+1] == scores[m])
                {
                    if (accuracy[m+1] > accuracy[m])
                    {
                        //Move player names around
                        strcpy(tempName, names[m]);
                        strcpy(names[m], names[m+1]);
                        strcpy(names[m+1], tempName);

                        //Sort score data
                        tempScore = scores[m];
                        scores[m] = scores[m+1];
                        scores[m+1] = tempScore;

                        //Move accuracy data around
                        tempAcc = accuracy[m];
                        accuracy[m] = accuracy[m+1];
                        accuracy[m+1] = tempAcc;

                        swaps = 1;
                    }
                }
            }
        } while(swaps);




        //Choose which leaderboard file to open for writing
        switch ((*Two).showMode())
        {
            case 1:
                ptr = SD.FOpen("LB_Easy.txt", "w");             //Opens Mode: Easy leaderboard file for writing
            break;

            case 2:
                ptr = SD.FOpen("LB_Med.txt", "w");              //Opens Mode: Easy leaderboard file for writing
            break;

            case 3:
                ptr = SD.FOpen("LB_Diff.txt", "w");             //Opens Mode: Easy leaderboard file for writing
            break;

            case 4:
                ptr = SD.FOpen("LB_Ins.txt", "w");              //Opens Mode: Easy leaderboard file for writing
            break;
        }

        //Write the sorted leaderboard data to the leaderboard file
        for (int j = 0; j<5; j++)
        {
            SD.FPrintf(ptr, "%s %d %d\n", names[j], scores[j], accuracy[j]);
        }

        SD.FClose(ptr);         //Close file
    }


    //Loop indefinitely
    while (loop == 0)
    {
        while(!LCD.Touch(&x, &y));                                  //Wait for touch
        if (x >= 80 && x <= 240) 
        {
            if (y >= 220 && y <= 239)
            {
                mainMenu(Two);                                      //Send to main menu
            } 

            else if (y >= 195 && y <= 215)
            {
                leaderMenu(Two);                                   //Send to leaderboard menu
            } 
        } 
    }

}


