/* Game Functions */

//Runs the game based on the time limit and circle radius
//Two - Pointer to Player object
//limit - Time limit for the game in seconds
//rad - Radius of the circles being drawn
//d - Level name as a string
//mode - A number 1-4 that represents the gamemode selected
void game(Player *Two, double limit, int rad, const char *d, int mode)
{
    //Initialize variables
    char difficulty[15] = "";                       //Name of difficulty
    strcpy(difficulty, d);                          //Copy difficulty name to character array difficulty
    int count = 0;                                  //Counter variable for an unlabelled function
    int randX, randY, accuracy = 0;                 //randX, randY - Random integer coordinates; accuracy - Player's accuracy stored as an integer
    double points = 0, total = 0;                   //points - Stores the number of circles hit; total - Stores the total number of circles shown
    double time = TimeNow();                        //Starting time
    LCD.Clear();
    LCD.SetFontColor(WHITE);
    (*Two).resetScore();                            //Set player score to 0

    LCD.Clear();                                    //Show the time limit and the difficulty
    LCD.SetDrawColor(GRAY);
    LCD.FillRectangle(60, 85, 200, 60);
    LCD.SetFontColor(WHITE);
    LCD.WriteAt("You have ", 80, 100);
    LCD.WriteAt((int)limit, 157, 100);
    LCD.WriteAt(" seconds", 180, 100);
    LCD.WriteAt("Difficulty: ", 85, 120);
    LCD.WriteAt(difficulty, 165, 120);
    double t = TimeNow();                           //Pause the screen for 3 seconds for the user to read the message
    while(TimeNow() - t < 3);
    LCD.Clear();

    //Loop until the time limit is reached
    while(TimeNow() - time < limit)
    {
        LCD.WriteAt("Score: ", 5, 10);                          //Display the current score, difficulty, and a quit button at the top of the screen
        LCD.WriteAt((int)points, 65, 10);
        if (mode == 3 && !abc)
        {   
            LCD.DrawCircle(140, 18, 5);
            LCD.WriteAt("M   de: ", 120, 10);
        }
        else
        {
            LCD.WriteAt("Mode: ", 120, 10);
        }
        LCD.WriteAt(difficulty, 180, 10);                    
        LCD.WriteAt("Quit", 280, 10);
        LCD.SetDrawColor(GRAY);
        LCD.DrawHorizontalLine(30, 0, maxX);

        if (count == 1)
        {
            LCD.WriteAt("Something happened...", 80, maxY/2-10);
        }
        else if (count == 2)
        {
            LCD.WriteAt("Try again maybe?", 100, maxY/2-10);
        }
        

        LCD.SetDrawColor(WHITE);                                //Set the circle color to white
                                                                //Note: The circle color was supposed to be random, but because some colors are harder to see than others
                                                                //      it was better to set the circle color to white only
        randX = RandInt() % (maxX-2*rad) + rad;                 //Generate random x-coordinate within the screen to draw circle
        randY = RandInt() % (maxY-40-2*rad) + rad + 40;         //Generate random y-coordinate within the screen to draw circle
        LCD.DrawCircle(randX, randY, rad);                      //Draw the circle

        while(!LCD.Touch(&x, &y))                               //Wait until user touch
        {
            if (TimeNow() - time >= limit)                      //If the time runs out, break out of the loop
            {
                break;
            }
        }

        if (TimeNow() - time < limit)                      //As long as there is still time, count score
        {
            //If the touch is within the boundaries of the circle (Technically a square boundary...)
            if ((x >= randX - rad && x <= randX + rad) && (y >= randY - rad && y <= randY + rad))
            {
                points++;           //Increment the points counter
            }

            //If the user decides to quit, return to main menu
            else if ((x >= 275 && x <= 339) && (y >= 5 && y <= 25))
            {
                mainMenu(Two);
            }

            //Some counter variable is incremented or something...
            else if ((x >= 135 && x <= 145) && (y >= 13 && y <= 23))
            {
                count++;
            }

            //Some unlabelled function is called or something...
            if (count == 3)
            {
                abc = true;
                unlabelledFunction1(Two);
            }

            total++;                                                //Increment the total circles counter
        }

        LCD.Clear();                                            //Clear screen
        LCD.ClearBuffer();                                      //Clear touch buffer to remove any extra touches

    }

    accuracy = points / total * 100.0;              //Calculate player's accuracy
    LCD.Clear();                                    //Clear screen and buffer
    LCD.ClearBuffer();
    LCD.WriteAt("Time's Up!", 120, maxY/2-10);      //Show "Time's Up!" on the screen for 3 seconds
    t = TimeNow();
    while(TimeNow() - t < 3);
    (*Two).setScore(points, accuracy);              //Save the player's score and accuracy 
    (*Two).setMode(mode);
    stats(Two, true);                               //Show the player's statistics
    
}



//Shows the level select menu
//Displays 4 buttons: Easy, Medium, Difficult, and Insane
//After one of the 4 buttons are pressed, this function sends the player into a game for the selected gamemode
