//Displays the leaderboard of the top 5 scores for the selected leaderboard (Easy, Medium, Difficult, or Insane)
//Two - Pointer to Player object
//mode - A number 1-4 that represents the gamemode leaderboard selected
void leaderboard(Player *Two, int mode)
{
    char names[6][13] = {"PlayerOne", "PlayerTwo", "PlayerThree", "PlayerFour", "PlayerFive", "Player"};
    int scores[6] = {0, 0, 0, 0, 0, 0};
    int accuracy[6] = {0, 0, 0, 0, 0, 0};
    
    LCD.Clear();
    LCD.SetFontColor(SCARLET);
    LCD.WriteAt("Leaderboard", 110, 10);
    LCD.SetDrawColor(GRAY);
    LCD.DrawHorizontalLine(30, 0, maxX);
    
    LCD.SetFontColor(WHITE);
    LCD.WriteAt("Name", 20, 40);
    LCD.WriteAt("Score", 160, 40);
    LCD.WriteAt("Accuracy", 220, 40);
    LCD.DrawHorizontalLine(55, 15, 75);
    LCD.DrawHorizontalLine(55, 155, 205);
    LCD.DrawHorizontalLine(55, 215, 295);
    
    FEHFile *fptr;

    switch (mode)
    {
        case 1:
            fptr = SD.FOpen("LB_Easy.txt", "a+");
        break;

        case 2:
            fptr = SD.FOpen("LB_Med.txt", "a+");
        break;

        case 3:
            fptr = SD.FOpen("LB_Diff.txt", "a+");
        break;

        case 4:
            fptr = SD.FOpen("LB_Ins.txt", "a+");
        break;
    }
    


    //Read in the leaderboard scores
    int i = 0;
    while (!SD.FEof(fptr))
    {
        
        SD.FScanf(fptr, "%s%d%d", names[i], &scores[i], &accuracy[i]);      //Scan in leaderboard
        {
            i++;
        }
    }

    SD.FClose(fptr);


    //If the file isn't filled with 5 rows of data, fill the arrays again to show properly on the leaderboard
    if (i < 4)
    {
        for (int k = 0; k < 5; k++)
        {
            strcpy(names[k],"Player");
            scores[k] = 0;
            accuracy[k] = 0;
        }
    }


    for (int j = 0; j < 5; j++)
    {
        LCD.WriteAt(names[j], 20, 65 + j * 25);
        LCD.WriteAt(scores[j], 175, 65 + j * 25);
        LCD.WriteAt(accuracy[j], 245, 65 + j * 25);
    }


    returnMain(Two);
}

//Function written by Jonathan Post
//Draws a QR code pixel art to the project website: https://u.osu.edu/sdp20201240j/
//Two - Pointer to Player object
//xo - Top left x-coordinate of pixel art
//yo - Top left y-coordinate of pixel art
void QRCode(Player *Two, int xo, int yo)
{
    LCD.Clear();                                        //Clear the screen
    LCD.SetDrawColor(GRAY);                             //Draw a gray rectangle
    LCD.DrawRectangle(80, 10, 160, 30);                 


    LCD.SetFontColor(SCARLET);                          //Set font color to scarlet
    LCD.WriteAt("Project Website", 100, 20);            //Write "Project Website"
    LCD.SetFontColor(WHITE);                            //Set font color to white
    LCD.WriteAt("Scan to visit our website", 70, 60);   //Prompt user to visit website

    int count = 0;
    int x,y;
    LCD.SetDrawColor(WHITE);                            //Set draw color to White
    LCD.FillRectangle(xo,yo,100,100);                   //Fill the rectangle with white

    LCD.SetDrawColor(BLACK);                            //Set the drawing color to black
    for (y = yo; y < yo + QRCODE_HEIGHT; y++)           //Fill the pixels to the correct height
    {
        for (x = xo; x < xo + QRCODE_WIDTH; x++)        //Fill the pixels to the correct width
        {
            if (qrcode[0][count] == BLACK)              //Draw black pixels
            {
                LCD.DrawPixel(x,y);                     //Draw the pixel in the correct color         
            }
            count++;                                    //Move through the entire color array
        }
    }

    returnMain(Two);

}





